INSERT INTO translations (language, key_path, value) VALUES
('pt-BR', 'experience.experiences.exp0.cargo', 'Desenvolvedor FullStack | Tech Lead | Sócio/Fundador'),
('pt-BR', 'experience.experiences.exp0.empresa', 'DocSend'),
('pt-BR', 'experience.experiences.exp0.localizacao', 'Florianópolis'),
('pt-BR', 'experience.experiences.exp0.periodo', '2025 – atual'),
('pt-BR', 'experience.experiences.exp0.atividades.0', 'Atuação como único desenvolvedor da empresa, responsável de ponta a ponta pelo ciclo de vida do produto (conceito, arquitetura, desenvolvimento, deploy, monitoramento e sustentação).'),
('pt-BR', 'experience.experiences.exp0.atividades.1', 'Liderança técnica como Tech Lead, definindo arquitetura, padrões de código, stack tecnológica e boas práticas de desenvolvimento para a plataforma.'),
('pt-BR', 'experience.experiences.exp0.atividades.2', 'Desenvolvimento de uma plataforma multitenant de gestão contábil, atendendo múltiplos escritórios/empresas dentro de uma mesma infraestrutura, com isolamento lógico de dados e configurações.'),
('pt-BR', 'experience.experiences.exp0.atividades.3', 'Construção de interfaces modernas e responsivas utilizando React + Vite e Material UI, com foco em usabilidade para rotinas contábeis (lançamentos, documentos, relatórios, integrações).'),
('pt-BR', 'experience.experiences.exp0.atividades.4', 'Implementação de APIs e serviços backend em Node.js e Java, seguindo boas práticas de arquitetura (REST, camadas, autenticação/autorização) e mantendo a segurança dos dados fiscais e contábeis.'),
('pt-BR', 'experience.experiences.exp0.atividades.5', 'Modelagem e manutenção do banco de dados PostgreSQL 18, incluindo estrutura de dados multitenant, otimização de consultas e criação de índices para garantir performance em volume crescente de informações.'),
('pt-BR', 'experience.experiences.exp0.atividades.6', 'Uso de Google Cloud (Cloud Run, Firebase, Cloud SQL) para deploy, escalabilidade e observabilidade do sistema, configurando ambientes, variáveis de ambiente, logs e monitoramento.'),
('pt-BR', 'experience.experiences.exp0.atividades.7', 'Definição e manutenção de pipelines de CI/CD, automatizando build, testes e deploy das aplicações, reduzindo erros manuais e acelerando a entrega de novas funcionalidades.'),
('pt-BR', 'experience.experiences.exp0.atividades.8', 'Implementação de mecanismos de autenticação, controle de acesso e segurança de dados sensíveis, considerando requisitos de compliance e sigilo de informações contábeis.'),
('pt-BR', 'experience.experiences.exp0.atividades.9', 'Criação de integrações com serviços externos (por exemplo, sistemas fiscais, emissão de documentos, providers de autenticação), incluindo consumo e disponibilização de APIs.'),
('pt-BR', 'experience.experiences.exp0.atividades.10', 'Definição e acompanhamento de métricas de uso da plataforma (engajamento, tempo de resposta, erros, custos de infraestrutura), apoiando a tomada de decisão sobre roadmap e otimização.'),
('pt-BR', 'experience.experiences.exp0.atividades.11', 'Criação de documentação técnica (arquitetura, endpoints, processos de deploy e operação) e material de apoio para clientes e equipe de negócio.'),
('pt-BR', 'experience.experiences.exp0.atividades.12', 'Participação ativa nas decisões estratégicas da empresa como sócio/fundador, contribuindo na definição de modelo de negócio, priorização de funcionalidades e visão de produto.');
INSERT INTO translations (language, key_path, value) VALUES
('pt-BR', 'experience.experiences.exp1.cargo', 'Analista de Sustentação Pleno'),
('pt-BR', 'experience.experiences.exp1.empresa', 'Nina Tecnologia'),
('pt-BR', 'experience.experiences.exp1.localizacao', 'Palhoça'),
('pt-BR', 'experience.experiences.exp1.periodo', '2025 – atual'),
('pt-BR', 'experience.experiences.exp1.atividades.0', 'Criação e manutenção de fluxos conversacionais e chatbots na plataforma NINA, utilizando React Flow, scripts em JavaScript e elaboração de prompts para integrações de chatbot com IA (modelos de linguagem e serviços cognitivos).'),
('pt-BR', 'experience.experiences.exp1.atividades.1', 'Análise de dados e criação de relatórios utilizando Metabase e SQL, gerando insights para áreas de negócio, acompanhamento de performance dos bots e identificação de oportunidades de melhoria.'),
('pt-BR', 'experience.experiences.exp1.atividades.2', 'Análise e resolução de bugs e incidentes críticos em ambiente de produção, garantindo a estabilidade, disponibilidade e desempenho da plataforma.'),
('pt-BR', 'experience.experiences.exp1.atividades.3', 'Monitoramento proativo de indicadores de saúde do ambiente (logs, alarmes, métricas de performance e uso dos fluxos de chatbot) para antecipar falhas e minimizar impactos ao cliente.'),
('pt-BR', 'experience.experiences.exp1.atividades.4', 'Condução de reuniões técnicas e estratégicas com clientes, levantamento de necessidades, definição de escopo e elaboração de planos de ação personalizados para evolução das jornadas conversacionais.'),
('pt-BR', 'experience.experiences.exp1.atividades.5', 'Criação e atualização de documentação técnica, manuais internos, base de conhecimento e procedimentos de troubleshooting, reduzindo o tempo médio de resolução e facilitando o repasse de conhecimento entre times.'),
('pt-BR', 'experience.experiences.exp1.atividades.6', 'Atuação conjunta com a equipe de Customer Success, produto e desenvolvimento para apoiar implantações, acompanhar indicadores de satisfação e priorizar melhorias e novas funcionalidades da plataforma NINA.'),
('pt-BR', 'experience.experiences.exp1.atividades.7', 'Gestão e sustentação da plataforma NINA no pós-implantação, com foco em melhoria contínua da experiência do usuário final e dos clientes corporativos.'),
('pt-BR', 'experience.experiences.exp1.atividades.8', 'Suporte técnico especializado para equipes técnicas dos clientes, apoiando na construção de novos projetos, integrações e implementação de novas features e facilidades.'),
('pt-BR', 'experience.experiences.exp1.atividades.9', 'Análise de integrações com serviços de terceiros, incluindo estudo e testes de APIs, validação de requisitos técnicos e apoio na definição de escopo de desenvolvimento.'),
('pt-BR', 'experience.experiences.exp1.atividades.10', 'Participação na análise de causa raiz (RCA) de incidentes, definição e acompanhamento de ações preventivas e corretivas, visando redução de recorrências.'),
('pt-BR', 'experience.experiences.exp1.atividades.11', 'Contribuição em projetos de melhoria de processos internos baseados em boas práticas (como ITIL), com foco em padronização de atendimentos, melhoria de fluxo entre times e ganho de eficiência operacional.'),
('pt-BR', 'experience.experiences.exp1.atividades.12', 'Colaboração na evolução de padrões de monitoração, observabilidade e logging da plataforma para facilitar diagnóstico e tomada de decisão em produção.');
INSERT INTO translations (language, key_path, value) VALUES
