INSERT INTO translations (language, key_path, value) VALUES
('es', 'experience.experiences.exp0.cargo', 'Desarrollador FullStack | Tech Lead | Socio/Fundador'),
('es', 'experience.experiences.exp0.empresa', 'DocSend'),
('es', 'experience.experiences.exp0.localizacao', 'Florianópolis'),
('es', 'experience.experiences.exp0.periodo', '2025 – actual'),
('es', 'experience.experiences.exp0.atividades.0', 'Actuación como único desarrollador de la empresa, responsable de extremo a extremo del ciclo de vida del producto (concepto, arquitectura, desarrollo, despliegue, monitoreo y mantenimiento).'),
('es', 'experience.experiences.exp0.atividades.1', 'Liderazgo técnico como Tech Lead, definiendo arquitectura, estándares de código, stack tecnológico y buenas prácticas de desarrollo para la plataforma.'),
('es', 'experience.experiences.exp0.atividades.2', 'Desarrollo de una plataforma multitenant de gestión contable, atendiendo múltiples oficinas/empresas dentro de una misma infraestructura, con aislamiento lógico de datos y configuraciones.'),
('es', 'experience.experiences.exp0.atividades.3', 'Construcción de interfaces modernas y responsivas utilizando React + Vite y Material UI, con enfoque en usabilidad para rutinas contables (asientos, documentos, informes, integraciones).'),
('es', 'experience.experiences.exp0.atividades.4', 'Implementación de APIs y servicios backend en Node.js y Java, siguiendo buenas prácticas de arquitectura (REST, capas, autenticación/autorización) y manteniendo la seguridad de los datos fiscales y contables.'),
('es', 'experience.experiences.exp0.atividades.5', 'Modelado y mantenimiento de la base de datos PostgreSQL 18, incluyendo estructura de datos multitenant, optimización de consultas y creación de índices para garantizar rendimiento con volumen creciente de información.'),
('es', 'experience.experiences.exp0.atividades.6', 'Uso de Google Cloud (Cloud Run, Firebase, Cloud SQL) para despliegue, escalabilidad y observabilidad del sistema, configurando entornos, variables de entorno, logs y monitoreo.'),
('es', 'experience.experiences.exp0.atividades.7', 'Definición y mantenimiento de pipelines de CI/CD, automatizando build, pruebas y despliegue de aplicaciones, reduciendo errores manuales y acelerando la entrega de nuevas funcionalidades.'),
('es', 'experience.experiences.exp0.atividades.8', 'Implementación de mecanismos de autenticación, control de acceso y seguridad de datos sensibles, considerando requisitos de cumplimiento y confidencialidad de información contable.'),
('es', 'experience.experiences.exp0.atividades.9', 'Creación de integraciones con servicios externos (por ejemplo, sistemas fiscales, emisión de documentos, proveedores de autenticación), incluyendo consumo y provisión de APIs.'),
('es', 'experience.experiences.exp0.atividades.10', 'Definición y seguimiento de métricas de uso de la plataforma (compromiso, tiempo de respuesta, errores, costos de infraestructura), apoyando la toma de decisiones sobre roadmap y optimización.'),
('es', 'experience.experiences.exp0.atividades.11', 'Creación de documentación técnica (arquitectura, endpoints, procesos de despliegue y operación) y material de apoyo para clientes y equipo de negocio.'),
('es', 'experience.experiences.exp0.atividades.12', 'Participación activa en decisiones estratégicas de la empresa como socio/fundador, contribuyendo en la definición de modelo de negocio, priorización de funcionalidades y visión de producto.');
INSERT INTO translations (language, key_path, value) VALUES
('es', 'experience.experiences.exp1.cargo', 'Analista de Sustentación Pleno'),
('es', 'experience.experiences.exp1.empresa', 'Nina Tecnologia'),
('es', 'experience.experiences.exp1.localizacao', 'Palhoça'),
('es', 'experience.experiences.exp1.periodo', '2025 – actual'),
('es', 'experience.experiences.exp1.atividades.0', 'Creación y mantenimiento de flujos conversacionales y chatbots en la plataforma NINA, utilizando React Flow, scripts en JavaScript y elaboración de prompts para integraciones de chatbot con IA (modelos de lenguaje y servicios cognitivos).'),
('es', 'experience.experiences.exp1.atividades.1', 'Análisis de datos y creación de informes utilizando Metabase y SQL, generando insights para áreas de negocio, seguimiento de rendimiento de bots e identificación de oportunidades de mejora.'),
('es', 'experience.experiences.exp1.atividades.2', 'Análisis y resolución de bugs e incidentes críticos en ambiente de producción, garantizando la estabilidad, disponibilidad y rendimiento de la plataforma.'),
('es', 'experience.experiences.exp1.atividades.3', 'Monitoreo proactivo de indicadores de salud del ambiente (logs, alarmas, métricas de rendimiento y uso de flujos de chatbot) para anticipar fallas y minimizar impactos al cliente.'),
('es', 'experience.experiences.exp1.atividades.4', 'Conducción de reuniones técnicas y estratégicas con clientes, levantamiento de necesidades, definición de alcance y elaboración de planes de acción personalizados para evolución de las jornadas conversacionales.'),
('es', 'experience.experiences.exp1.atividades.5', 'Creación y actualización de documentación técnica, manuales internos, base de conocimiento y procedimientos de troubleshooting, reduciendo el tiempo promedio de resolución y facilitando el traspaso de conocimiento entre equipos.'),
('es', 'experience.experiences.exp1.atividades.6', 'Actuación conjunta con el equipo de Customer Success, producto y desarrollo para apoyar implementaciones, acompañar indicadores de satisfacción y priorizar mejoras y nuevas funcionalidades de la plataforma NINA.'),
('es', 'experience.experiences.exp1.atividades.7', 'Gestión y sustentación de la plataforma NINA en post-implementación, con enfoque en mejora continua de la experiencia del usuario final y de los clientes corporativos.'),
('es', 'experience.experiences.exp1.atividades.8', 'Soporte técnico especializado para equipos técnicos de los clientes, apoyando en la construcción de nuevos proyectos, integraciones e implementación de nuevas features y facilidades.'),
('es', 'experience.experiences.exp1.atividades.9', 'Análisis de integraciones con servicios de terceros, incluyendo estudio y pruebas de APIs, validación de requisitos técnicos y apoyo en la definición de alcance de desarrollo.'),
('es', 'experience.experiences.exp1.atividades.10', 'Participación en el análisis de causa raíz (RCA) de incidentes, definición y seguimiento de acciones preventivas y correctivas, visando reducción de recurrencias.'),
('es', 'experience.experiences.exp1.atividades.11', 'Contribución en proyectos de mejora de procesos internos basados en buenas prácticas (como ITIL), con enfoque en estandarización de atendimientos, mejora de flujo entre equipos y ganancia de eficiencia operacional.'),
('es', 'experience.experiences.exp1.atividades.12', 'Colaboración en la evolución de estándares de monitoreo, observabilidad y logging de la plataforma para facilitar diagnóstico y toma de decisiones en producción.');
INSERT INTO translations (language, key_path, value) VALUES
